<?php include "../Include/Inc_JavaScript.php"; ?><script language="javascript">	$(document).ready(function(){		$('.botao').click(function(){			$('#tabela tbody>tr:last').clone(true).insertAfter('#tabela tbody>tr:last');		});				var origem = "";				$('.add').click(function(){				if(($('#tabela tr:last select:eq(0)').val() == $('#tabela tr:last select:eq(1)').val()))				{					alert("A origem não pode ser igual ao destino!");					return false;				}							var clone = $('#tabela tr:last').clone(true); // Clona a ultima linha				var select1 = $('#tabela tr:last select:eq(1)').val(); // Valor de destino que se transformara 				if($('#tabela tr').length == 2)					origem = $('#tabela tr:last select:eq(0)').val();								$('#tabela tr:last select').attr("disabled", "disabled");				$('#tabela tr:last input:eq(0)').attr("disabled", "disabled");								if(origem != select1)				{					clone.insertAfter('#tabela tbody>tr:last');									$('#tabela tr:last select:eq(0)').val(select1);					$('#tabela tr:last select:eq(1)').val(origem);					$('#tabela tr:last select:eq(0)').attr("disabled", "disabled");				}		});				$('.remove').click(function(){			if($('#tabela tr').length > 2)			{				$('#tabela tr:last').remove();				if($('#tabela tr').length == 2)					$('#tabela tr:last select').attr("disabled", "");				else					$('#tabela tr:last select:eq(1)').attr("disabled", "");				$('#tabela tr:last input:eq(0)').attr("disabled", "");			}		});				$('.num').click(function(){			alert($('#tabela tr').length-1);		});			});</script><table id="tabela" border="1">	<tbody>		<tr>			<th>De</th>			<th>Para</th>			<th>&nbsp;</th>		</tr>		<tr>			<td>				<select>					<option>Salvador</option>					<option>Jacobina</option>					<option>Miguel Calmon</option>					<option>Morro do Chapeu</option>				</select>			</td>						<td>				<select>					<option>Salvador</option>					<option>Jacobina</option>					<option>Miguel Calmon</option>					<option>Morro do Chapeu</option>				</select>			</td>			<td><input class="add" type="button" value="Adicionar" /><!--<input class="remove" type="button" value="Remove" />--></td>		</tr>			<tbody></table><input class="botao" type="button" value="Add" /><input class="remove" type="button" value="Remove" /><input class="num" type="button" value="Numero de linhas" />